import React, { useState } from "react";

const ResumeAnalyzer = () => {
  const [resumeFile, setResumeFile] = useState(null);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setResumeFile(e.target.files[0]);
  };

  const handleAnalyze = async () => {
    if (!resumeFile) return alert("Please upload a resume first.");
    const formData = new FormData();
    formData.append("resume", resumeFile);

    setLoading(true);
    try {
      const response = await fetch("http://localhost:5000/api/analyze-resume", {
        method: "POST",
        body: formData,
      });

      const result = await response.json();
      setAnalysisResult(result);
    } catch (error) {
      console.error("Analysis failed:", error);
      alert("Failed to analyze resume.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white shadow rounded mt-10">
      <h2 className="text-2xl font-semibold mb-4 text-center text-blue-600">AI Resume Analyzer</h2>
      <input
        type="file"
        accept=".pdf,.doc,.docx"
        onChange={handleFileChange}
        className="mb-4 w-full border border-gray-300 p-2 rounded"
      />
      <button
        onClick={handleAnalyze}
        disabled={loading}
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 w-full"
      >
        {loading ? "Analyzing..." : "Analyze Resume"}
      </button>

      {analysisResult && (
        <div className="mt-6 border-t pt-4">
          <h3 className="text-xl font-bold text-gray-700 mb-2">Analysis Result:</h3>
          <p><strong>Match Percentage:</strong> {analysisResult.match}%</p>
          <p><strong>Skills:</strong> {analysisResult.skills.join(", ")}</p>
          <p><strong>Suggestions:</strong> {analysisResult.suggestions}</p>
        </div>
      )}
    </div>
  );
};

export default ResumeAnalyzer;
