import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import JobPostPage from "./pages/JobPostPage";      // ➤ Job posting form
import CandidateProfile from "./pages/CandidateProfile";  // ➤ Candidate profile
import JobList from "./pages/JobList";              // ➤ Job listing page
import JobDetail from "./pages/JobDetail";
import Login from "./pages/Login";
import Register from "./pages/Register";

function HomePage() {
  return (
    <section className="text-center py-20 px-4">
      <h2 className="text-4xl font-bold text-gray-800 mb-4">Find Your Dream Job</h2>
      <p className="text-gray-600 text-lg mb-6">
        Connect with professionals, apply for jobs, and grow your career.
      </p>
      <div className="space-x-4">
        <Link to="/jobs">
          <button className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700">
            Browse Jobs
          </button>
        </Link>
        <Link to="/post-job">
          <button className="border border-blue-600 text-blue-600 px-6 py-2 rounded hover:bg-blue-50">
            Post a Job
          </button>
        </Link>
      </div>
    </section>
  );
}

function App() {
  return (
    <Router>
      <div className="min-h-screen bg-gray-50">
        {/* Navbar */}
        <nav className="bg-white shadow px-6 py-4 flex justify-between items-center">
          <h1 className="text-xl font-bold text-blue-600">JobConnect</h1>
          <div className="space-x-4">
            <Link to="/" className="text-gray-700 hover:text-blue-600">Home</Link>
            <Link to="/jobs" className="text-gray-700 hover:text-blue-600">Jobs</Link>
            <Link to="/profile" className="text-gray-700 hover:text-blue-600">Profile</Link>
            <Link to="/post-job" className="text-gray-700 hover:text-blue-600">Post</Link>
            <button className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">Login</button>
          </div>
        </nav>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/jobs" element={<JobList />} />           {/* Job listing */}
          <Route path="/jobs/:id" element={<JobDetail />} />
          <Route path="/post-job" element={<JobPostPage />} />    {/* Job posting */}
          <Route path="/profile" element={<CandidateProfile />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
