import React, { useEffect, useState } from "react";
import axios from "axios";

const CandidateProfile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("http://localhost:5000/api/profile", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setUser(res.data.user);
      } catch (err) {
        console.error("Failed to fetch user:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, []);

  if (loading) return <p className="text-center mt-10">Loading profile...</p>;
  if (!user) return <p className="text-center mt-10 text-red-500">Failed to load user data.</p>;

  return (
    <div className="max-w-2xl mx-auto bg-white p-6 mt-10 shadow rounded">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-blue-700">Candidate Profile</h2>
        <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Edit</button>
      </div>

      <div className="mb-4">
        <label className="font-semibold">Full Name:</label>
        <p>{user.name}</p>
      </div>

      <div className="mb-4">
        <label className="font-semibold">Email:</label>
        <p>{user.email}</p>
      </div>

      <div className="mb-4">
        <label className="font-semibold">Phone:</label>
        <p>{user.phone || "Not provided"}</p>
      </div>

      <div className="mb-4">
        <label className="font-semibold">Skills:</label>
        <ul className="list-disc list-inside">
          {user.skills?.length > 0 ? (
            user.skills.map((skill, idx) => <li key={idx}>{skill}</li>)
          ) : (
            <li>No skills listed</li>
          )}
        </ul>
      </div>

      <div className="mb-4">
        <label className="font-semibold">About Me:</label>
        <p>{user.bio || "No bio provided"}</p>
      </div>
    </div>
  );
};

export default CandidateProfile;
